    constructor(props) {
        super(props);
        this.state = {
            // isLoading: true,
        }
    }

    componentDidMount() {
        const film = Math.floor(Math.random() * 6) + 1;
        fetch(`${base_url}/v1/films/${film}`)
            .then(response => response.json())
            .then(data => this.setState({text: data.opening_crawl})
            )
        this.props.changeLoading();
    }

    render() {
        return (
            // this.state.isLoading ? <Loader/> : <p className="farGalaxy">{this.state.text}</p>
            <p className="farGalaxy">{this.state.text}</p>
        );
    }


};

export default FarGalaxy;
