import React, {Component} from 'react';
import {base_url} from "../utils/constants";
import Loader from "./Loader";

class FarGalaxy extends Component {
    constructor(props) {
        super(props);
        this.state = {
            isLoading: true,
            // text: "",
        }
    }

    componentDidMount() {
        const film = Math.floor(Math.random() * 6) + 1;
        fetch(`${base_url}/v1/films/${film}`)
            .then(response => response.json())
            .then(data => this.setState({text: data.opening_crawl, isLoading: !this.state.isLoading})
            )
    }

    render() {
        return (
            this.state.isLoading ? <Loader/> : <p className="farGalaxy">{this.state.text}</p>
        );
    }


};

export default FarGalaxy;
